mapper.update:
UPDATE
		CART
   SET
   		BUY_Q = BUY_Q + #{buyQ}
   	  , B_PRICE = B_PRICE + #{buyPrice}
   	  , ENROLL_DATE = SYSDATE
 WHERE
 		ID = #{id}
   AND  P_CODE = #{pcode}

mapper.insert:
INSERT
INTO S_CATEGORY
(
